import React, { useState } from "react";
import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { auth, provider } from "./Firebase";
import { NavLink, useNavigate } from "react-router-dom";
import GoogleLogo from '../img/Google.png'; // Ensure the path is correct
import "../style/Login.css";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (event) => {
    event.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      navigate("/dashboard"); // Redirect to a dashboard or home page
      console.log(userCredential.user);
    } catch (error) {
      alert("Invalid email or password");
      console.error(error.code, error.message);
    }
  };

  const handleGoogleLogin = async (event) => {
    event.preventDefault();
    try {
      const result = await signInWithPopup(auth, provider);
      navigate("/dashboard"); // Redirect to a dashboard or home page
      console.log(result.user);
    } catch (error) {
      console.error(error.code, error.message);
    }
  };

  return (
    <>
    <main className="main-container">
      <section className="login-section">
        <div className="login-container">
          <p className="app-title"> FocusApp </p>

          <form className="login-form">
            <div className="form-group">
              <label htmlFor='email-address'>Email address</label>
              <input
                id='email-address'
                name='email'
                type='email'
                required
                placeholder='Email address'
                onChange={(e) => setEmail(e.target.value)}
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor='password'>Password</label>
              <input
                id='password'
                name='password'
                type='password'
                required
                placeholder='Password'
                onChange={(e) => setPassword(e.target.value)}
                className="form-input"
              />
            </div>

            <div className="button-group">
              <button onClick={onLogin} className="login-button">Login</button>
              <button type="button" className="google-signup-button" onClick={signInWithGoogle}>
            <img src={GoogleLogo} alt="Google" className="google-logo" />
            Regístrame con Google
          </button>
            </div>
          </form>

          <p className='signup-prompt text-sm text-white text-center'>
            No account yet? <NavLink to='/signup' className="signup-link">Sign up</NavLink>
          </p>
        </div>
      </section>
    </main>
  </>  
  );
};

export default Login;
