import React, { useState } from "react";
import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { auth, provider } from "./Firebase";
import { NavLink, useNavigate } from "react-router-dom";
import GoogleLogo from '../img/Google.png'; // Make sure the path is correct
import "../style/Login.css";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const onLogin = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      // Signed in
      navigate("/dashboard"); // Redirect to the dashboard or home page
    } catch (error) {
      alert(error.message); // Display a more user-friendly message
    }
  };

  const onGoogleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithPopup(auth, provider);
      navigate("/dashboard"); // Redirect to the dashboard or home page
    } catch (error) {
      alert(error.message); // Display a more user-friendly message
    }
  };

  return (
    <div className="main-container">
      <section className="login-section">
        <div className="login-container">
          <h1 className="app-title">FocusApp</h1>

          <form className="login-form" onSubmit={onLogin}>
            <div className="form-group">
              <label htmlFor='email-address'>Email address</label>
              <input
                id='email-address'
                type='email'
                required
                placeholder='Email address'
                onChange={(e) => setEmail(e.target.value)}
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor='password'>Password</label>
              <input
                id='password'
                type='password'
                required
                placeholder='Password'
                onChange={(e) => setPassword(e.target.value)}
                className="form-input"
              />
            </div>

            <button type="submit" className="login-button">Login</button>
          </form>

          <button type="button" className="google-signup-button" onClick={onGoogleLogin}>
            <img src={GoogleLogo} alt="Google" className="google-logo" />
            Reg√≠strame con Google
          </button>

          <p className='signup-prompt'>
            No account yet? <NavLink to='/signup' className="signup-link">Sign up</NavLink>
          </p>
        </div>
      </section>
    </div>
  );
};

export default Login;
